import React, { useEffect } from 'react';
import { Grid,Paper } from '@material-ui/core'
import Controls from '../../../components/controls/Controls';
import { useFormL, Form } from '../../../components/useFormL';
import { CircularProgress } from '@material-ui/core';
import { connect } from 'react-redux';
import { authenticate, authFailure, authSuccess } from '../../../redux/authActions';
import * as authService from '../../../services/authService'

const initialValues = {
    username: '',
    password: '',
    loading: false,
    message: '',
    checkedB: false

}

export default function Login(props) {
    //const { addOrEdit, recordForEdit } = props

    const validate = (fieldValues = values) => {
        let temp = { ...errors }
        if ('username' in fieldValues)
            temp.username = fieldValues.username ? "" : "This field is required."
        if ('password' in fieldValues)
            temp.password = fieldValues.password ? "" : "This field is required."
        setErrors({
            ...temp
        })

        if (fieldValues === values)
            return Object.values(temp).every(x => x === "")
    }

    const handleSubmit = e => {
        e.preventDefault();
        props.authenticate()

        if (validate()) {
            values.loading = true
        }
    }

    const {
        values,
        setValues,
        errors,
        setErrors,
        handleInputChange,
        resetForm
    } = useFormL(initialValues, true, validate);

    // useEffect(() => {
    //     if (recordForEdit !== null)
    //         setValues({
    //             ...recordForEdit
    //         })
    // }, [recordForEdit])

    const gridStyle = {paddingLeft:'170px', margin:'0px'}
    const img = {position:'center', padding:'0px', with:'120px', margin:'0rem'};
    const paperStyle={padding:'20px', height:'75vh', width:350, margin:"50px"}
    const btnstyle={margin:'8px 0'}

    return (
        <>
            <Form onSubmit={handleSubmit}>
                <Grid style={gridStyle}>
                    <Paper style={paperStyle}>
                        <Grid align='center'>
                            <img style={img} src='logo.png'/>
                            <h2>ENTRAR</h2>
                        </Grid>
                        <Controls.textField
                            label='Usuário' 
                            placeholder='Insira usuário' 
                            name='username'
                            fullWidth
                            value={values.username}
                            onChange={handleInputChange}
                            error={errors.username}
                        /><br/><br/>
                        <Controls.textField 
                            label='Senha' 
                            placeholder='Insira senha' 
                            name='password'
                            type='password' 
                            fullWidth
                            value={values.password}
                            onChange={handleInputChange}
                            error={errors.password} 
                        />
                        <br/><br/>
                        <Controls.Checkbox 
                        name='checkedB'
                        label="Lembre de mim"
                        checked={values.checkedB}
                        onChange={handleInputChange}
                        value={values.checkedB}
                        /><br/><br/>
                        <div style={btnstyle}>
                            <Controls.Button
                                type='submit' 
                                variant="contained" 
                                text={values.loading ? (<CircularProgress />) : 'ENTRAR'}
                                disabled={values.loading}
                                fullWidth
                            />
                        </div>
                    </Paper>
                </Grid>
            </Form>
        </>
    );
}
