import React, { useEffect } from 'react'
import { Grid, } from '@material-ui/core';
import Controls from "../../../components/controls/Controls";
import { FormF, useFormF } from '../../../components/useFormF';


const initialFValues = {
    montant_max: '',
    montant_min: '',
    fee: '',
    date_input: new Date().toISOString().split('T')[0],
    date_output: new Date().toISOString().split('T')[0],
    deadline:''
}

export default function FeeForm(props) {
    const { addOrEdit, recordForEdit } = props

    const validate = (fieldValues = values) => {
        let temp = { ...errors }
        if ('montant_max' in fieldValues)
            temp.montant_max = fieldValues.montant_max ? "" : "Este campo e obrigatorio."
        if ('montant_min' in fieldValues)
            temp.montant_min = fieldValues.montant_min ? "" : "Este campo e obrigatorio."
        if ('fee' in fieldValues)
            temp.fee = fieldValues.fee ? "" : "Este campo e obrigatorio."
        setErrors({
            ...temp
        })

        if (fieldValues === values)
            return Object.values(temp).every(x => x === "")
    }

    const {
        values,
        setValues,
        errors,
        setErrors,
        handleInputChange,
        resetForm
    } = useFormF(initialFValues, true, validate);

    const handleSubmit = e => {
        e.preventDefault()
        if (validate()) {
            addOrEdit(values, resetForm);
        }
    }

    useEffect(() => {
        if (recordForEdit !== null)
            setValues({
                ...recordForEdit
            })
    }, [recordForEdit])

    return (
        <FormF onSubmit={handleSubmit}>
            <Grid container>
                <Grid item xs={6}>
                    <Controls.Input
                        name="montant_max"
                        label="Quantia Max"
                        value={values.montant_max}
                        onChange={handleInputChange}
                        error={errors.montant_max}
                    />
                    <Controls.Input
                        label="Quantia Min"
                        name="montant_min"
                        value={values.montant_min}
                        onChange={handleInputChange}
                        error={errors.montant_min}
                    />
                    <Controls.Input
                        name="fee"
                        label="Taxa"
                        value={values.fee}
                        onChange={handleInputChange}
                        error={errors.fee}
                    />
                </Grid>
                <Grid item xs={6}>
                    <Controls.Input
                        label="Data Inicio"
                        name="date_input"
                        type='date'
                        value={values.date_input}
                        onChange={handleInputChange}
                    />
                    <Controls.Input
                        name="date_output"
                        label="Data Final"
                        type='date'
                        value={values.date_output}
                        onChange={handleInputChange}
                        error={errors.date_output}
                    />

                    <div>
                        <Controls.Button
                            type="submit"
                            text="CRIO" />
                        <Controls.Button
                            text="REDEFINIR"
                            color="default"
                            onClick={resetForm} />
                    </div>
                </Grid>
            </Grid>
        </FormF>
    )
}
