/* eslint-disable react-hooks/exhaustive-deps */
/* eslint-disable no-unused-vars */
import React, { useEffect } from "react";
import { useFormAdmin, Form } from "../../../components/useFormAdmin";
import { Grid, makeStyles, } from '@material-ui/core';
import Controls from "../../../components/controls/Controls";
import * as adminService from "../../../services/adminService";
import { useState } from "react";

const initialeValues = {
    logistic_type: '',
    partner:'',
    agent:'',
    staff:'',
    searchby: '',
    disabled: true

}

const useStyles = makeStyles(theme => ({
    searchInput: {
        width: '90%',
    },
}))

const searchBy = () => ([
    { id: 'waybill', title: 'WayBill' },
    { id: 'materialName', title: 'Material Name' },
    { id: 'referenceMaterial', title: 'Reference Material' }
])

const cars = [
    { id: 1, model: "CRV", company: "Honda" },
    { id: 2, model: "Accord", company: "Honda" },
    { id: 3, model: "800", company: "Maruti" },
    { id: 4, model: "Civic", company: "Honda" },
    { id: 5, model: "Model S", company: "Tesla" }
]

export default function TransferForm(props) {
    const { addOrEdit, recordForEdit } = props;
    const [search, setSearch] = useState(null);
    const [currentUser, setCurrentUser] = useState(adminService.getCurrentUser());
    
    const validate = (fieldValues = values) => {
        let temp = { ...errors }
        if ('logistic_type' in fieldValues)
            temp.logistic_type = fieldValues.logistic_type ? "" : "This field is required."
        if ('partner' in fieldValues)
            temp.partner = fieldValues.partner ? "" : "This field is required."
        if ('agent' in fieldValues)
            temp.agent = fieldValues.agent ? "" : "This field is required."
        if ('staff' in fieldValues)
            temp.staff = fieldValues.staff ? "" : "This field is required."
        setErrors({
            ...temp
        })

        if (fieldValues === values)
            return Object.values(temp).every(x => x === "")
    }

    const handleSubmit = e => {
        e.preventDefault()
        if (validate()) {
            addOrEdit(values, resetForm);
        }
    }

    const handleSearch = e => {
        // let target = e.target;
        // setFilterFn({
        //     fn: records => {
        //         if (target.value === "")
        //             return records;
        //         else
        //             return records.filter(x => x.eglise.toLowerCase().includes(target.value))
        //     }
        // })
    }

    const {
        values,
        setValues,
        errors,
        setErrors,
        handleInputChange,
        resetForm
    } = useFormAdmin(initialeValues, true, validate);

    useEffect(() => {
        if (recordForEdit !== null)
            setValues({
                ...recordForEdit
            })
    }, [recordForEdit])

    const classes = useStyles();
    

    return (
        <Form onSubmit={handleSubmit}>
            <Grid container>
                {/* <Grid item xs={6}>
                    <Controls.Select
                        name="logistic_type"
                        label="Select Logistic type"
                        value={values.logistic_type}
                        onChange={handleInputChange}
                        options={adminService.getAutorisation()}
                        error={errors.logistic_type}
                    />
                    <Controls.Select
                        label="Select partner"
                        name="partner"
                        value={values.partner}
                        onChange={handleInputChange}
                        options={adminService.getAutorisation()}
                        error={errors.partner}
                    />
                </Grid> */}
                <Grid item xs={6}>
                    {/* <Controls.Select
                        name="agent"
                        label="Select Agent"
                        value={values.agent}
                        onChange={handleInputChange}
                        options={adminService.getAutorisation()}
                        error={errors.agent}
                    />
                    <Controls.Select
                        name="staff"
                        label="Select staff"
                        value={values.staff}
                        onChange={handleInputChange}
                        options={adminService.getAutorisation()}
                        error={errors.staff}
                    /> */}
                    <Controls.RadioGroup
                        label='Search By'
                        name="searchby"
                        value={values.searchby}
                        onChange={handleInputChange}
                        items={searchBy()}
                    />
                    <datalist  
   options = {cars}
   id = "id"  
   value1 = "model" 
   value2="company" 
   selectedIdName = "selectedCar" 
   selectedId="" 
   onOptionChange = {this.handleChange}  />
                    <div> 
                        <Controls.Button
                            type="submit"
                            text="Search" 
                            disabled={(!values.searchby) ? true : false}
                        />
                    </div>
                </Grid>
            </Grid>
        </Form>
    )
}
