import axios from "axios";

const ASSOCIATION_API_BASE_URL = "http://localhost:8080/users";
const headers = {
    'Content-Type': 'application/json'
}

export const getStatus = () => ([
    { id: 'ENABLE', title: 'ENABLE' },
    { id: 'DISABLE', title: 'DISABLE' }
])

export const getAutorisation = () => ([
    { id: 'ENABLE', title: 'ENABLE' },
    { id: 'DISABLE', title: 'DISABLE' }
])


export function insertAdmin(user) {
    return axios.post(ASSOCIATION_API_BASE_URL, user, {headers})
}

export function getAllAdmin() {
    return axios.get(ASSOCIATION_API_BASE_URL);
 }
