import axios from "axios";

const ASSOCIATION_API_BASE_URL = "http://localhost:8080/users";
const headers = {
    'Content-Type': 'application/json'
}

export const getStatus = () => ([
    { id: 'PEMIRTIR', title: 'Pemirtir' },
    { id: 'DESATIVAR', title: 'Desativar' }
])

export const getAutorisation = () => ([
    { id: 'SUP_1', title: 'BAIXO' },
    { id: 'SUP_2', title: 'MEDIO' },
    { id: 'SUP_3', title: 'SUPERIOR' }
])


export function insertAdmin(user) {
    return axios.post(ASSOCIATION_API_BASE_URL, user, {headers})
}

export function getAllAdmin() {
    return axios.get(ASSOCIATION_API_BASE_URL);
}

export function getAdminDisable() {
    return axios.get(ASSOCIATION_API_BASE_URL+"/disable");
}

export function updateAdmin(userId, user) {
    return axios.put(ASSOCIATION_API_BASE_URL + '/' + userId, user, {headers})
}
