import axios from "axios";

const ASSOCIATION_API_BASE_URL = "http://localhost:8080/associations";
const headers = {
    'Content-Type': 'application/json'
}

export const getDepartmentCollection = () => ([
    { id: 'ENABLE', title: 'ENABLE' },
    { id: 'DISABLE', title: 'DISABLE' }
])

export function insertAssociation(association) {
    return axios.post(ASSOCIATION_API_BASE_URL, association, {headers})
}

export function updateAssociation(associationId, association) {
    return axios.put(ASSOCIATION_API_BASE_URL + '/' + associationId, association, {headers})
}

export function deleteEmployee(associationId) {
    return axios.put(ASSOCIATION_API_BASE_URL + '/' + associationId)
}

export function generateEmployeeId(associationId) {
    return axios.get(ASSOCIATION_API_BASE_URL + '/' + associationId)
}

export function getAllAssociation() {
   return axios.get(ASSOCIATION_API_BASE_URL);
}
