import React from 'react';
import { Grid,Paper, Avatar, TextField, Button, Typography,Link } from '@material-ui/core'
import LockOutlinedIcon from '@material-ui/icons/LockOutlined';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import Checkbox from '@material-ui/core/Checkbox';
import Controls from '../../../components/controls/Controls';


const style = {
    sideMenu: {
        display: 'flex',
        flexDirection: 'column',
        position: 'absolute',
        left: '0px',
        width: '250px',
        height: '100%',
        backgroundColor: '#f4661b',
        color: "white",
        
    },
    img: {
        position: 'center',
        padding: '0px',
        width: '150px',
        margin: '2rem'
        
    }
}

function Login() {
    const classes = style();
    const paperStyle={padding :20,height:'70vh',width:300, margin:"30px auto"}
    const avatarStyle={backgroundColor:'#1bbd7e'}
    const btnstyle={margin:'8px 0'}

    return (
        <>
            <Grid>
            <Paper elevation={5} style={paperStyle}>
                <Grid align='center'>
                    <img className={classes.img} src='logo.png'/>
                    <h2>Sign In</h2>
                </Grid>
                <TextField label='Username' placeholder='Enter username' fullWidth required/><br/><br/>
                <TextField label='Password' placeholder='Enter password' type='password' fullWidth required/>
                <br/><br/>
                <FormControlLabel
                    control={
                    <Checkbox
                        name="checkedB"
                        color="primary"
                    />
                    }
                    label="Remember me"
                 />
                <Controls.Button 
                    color='primary' 
                    variant="contained" 
                    text='SIGN IN'
                    style={btnstyle} fullWidth
                />
                {/* <Typography >
                     <Link href="#" >
                        Forgot password ?
                </Link>
                </Typography>
                <Typography > Do you have an account ?
                     <Link href="#" >
                        Sign Up 
                </Link>
                </Typography> */}
            </Paper>
        </Grid>
        </>
    );
}

export default Login;
