import './dashboard.css'
import { LeakAdd, LiveHelp, LocalShipping, Settings } from "@material-ui/icons";
import * as membreService from '../../services/membreService';
import { useState } from 'react';
import { useEffect } from 'react';
import { Redirect } from 'react-router-dom';

const Widget = ({ type, redirect }) => {
const [sumMembre, setSumMembre] = useState([]);
const [currentUser, setCurrentUser] = useState(membreService.getCurrentUser());

const getmembre = () => {
  (currentUser ? (currentUser.roles.toString() === "ROLE_LOCAL") ? 
  (membreService.getSumMembre(currentUser.matricule)) : (membreService.getSum()) : membreService.getSum())
  .then((res) => {
    setSumMembre(res.data)
  });
}

useEffect(() => {
  getmembre();
}, []);

    let data; 

     //temporary
  const user = (sumMembre);
  const diff = 20;
  const empres = 10;
  const reemb = 6;
  const resta = 4;
  switch (type) {
    case "user":
      data = {
        title: "S & L",
        Money: user,
        link: "Supply and Logistics",
        icon: (
          <LocalShipping
            className="icon"
            fontSize='large'
            style={{
              backgroundColor: "rgba(160, 160, 160, 0.349)",
            }}
          />
        ),
      };
      break;
    case "order":
      data = {
        title: "EUM",
        Money: empres,
        link: "End User Monitoring",
        icon: (
          <LiveHelp
            className="icon"
            fontSize='large'
            style={{
              backgroundColor: "rgba(160, 160, 160, 0.349)",
            }}
          />
        ),
      };
      break;
    case "earning":
      data = {
        title: "PMV",
        Money: reemb,
        link: "Performance Monitoring Visit",
        icon: (
          <LeakAdd
            className="icon"
            fontSize='large'
            style={{ backgroundColor: "rgba(160, 160, 160, 0.349)",}}
          />
        ),
      };
      break;
    case "balance":
      data = {
        title: "Settings",
        Money: resta,
        link: "Configure the application",
        icon: (
          <Settings
            className="icon"
            style={{
              backgroundColor: "rgba(160, 160, 160, 0.349)",
            }}
            fontSize='large'
          />
        ),
      };
      break;
    default:
      break;
  }

    const handleNext = () => {
      if(redirect) {
        <Redirect to={redirect} />
      }

    }
    return (
        <div className='widget'>
            <div onClick={handleNext} className='left'>
              <div className='duo'>
                <span className='title'>{data.icon}</span>
                <span className='counter'>{data.title} </span>
              </div>
              <span className='link'>{data.link}</span>
            </div>
        </div>
    );
  };
  
  export default Widget;
