import axios from "axios";

const ASSOCIATION_API_BASE_URL = "http://localhost:8080/membres";
const headers = {
    'Content-Type': 'application/json'
}

export const getStatus = () => ([
    { id: 'PEMIRTIR', title: 'Pemirtir' },
    { id: 'DESATIVAR', title: 'Desativar' }
])


