import './dashboard.css'
import { AccountBalanceWalletOutlined, KeyboardArrowUp, MonetizationOnOutlined, PersonOutlined, ShoppingCartOutlined } from "@material-ui/icons";
import * as membreService from '../../services/membreService';
import { useState } from 'react';
import { useEffect } from 'react';
import { Redirect } from 'react-router-dom';

const Widget = ({ type, redirect }) => {
const [sumMembre, setSumMembre] = useState([]);
const [currentUser, setCurrentUser] = useState(membreService.getCurrentUser());

const getmembre = () => {
  (currentUser ? (currentUser.roles.toString() === "ROLE_LOCAL") ? 
  (membreService.getSumMembre(currentUser.matricule)) : (membreService.getSum()) : membreService.getSum())
  .then((res) => {
    setSumMembre(res.data)
  });
}

useEffect(() => {
  getmembre();
}, []);

    let data; 

     //temporary
  const user = (sumMembre);
  const diff = 20;
  const empres = 10;
  const reemb = 6;
  const resta = 4;
  switch (type) {
    case "user":
      data = {
        title: "Logistics",
        Money: user,
        link: "Transfer from IP",
        icon: (
          <PersonOutlined
            className="icon"
            style={{
              color: "crimson",
              backgroundColor: "rgba(255, 0, 0, 0.2)",
            }}
          />
        ),
      };
      break;
    case "order":
      data = {
        title: "Semaine",
        Money: empres,
        link: "Encaissement semaine",
        icon: (
          <ShoppingCartOutlined
            className="icon"
            style={{
              backgroundColor: "rgba(218, 165, 32, 0.2)",
              color: "goldenrod",
            }}
          />
        ),
      };
      break;
    case "earning":
      data = {
        title: "Mois",
        Money: reemb,
        link: "Encaissement mois",
        icon: (
          <MonetizationOnOutlined
            className="icon"
            style={{ backgroundColor: "rgba(0, 128, 0, 0.2)", color: "green" }}
          />
        ),
      };
      break;
    case "balance":
      data = {
        title: "Année",
        Money: resta,
        link: "Encaissement dans l'année",
        icon: (
          <AccountBalanceWalletOutlined
            className="icon"
            style={{
              backgroundColor: "rgba(128, 0, 128, 0.2)",
              color: "purple",
            }}
          />
        ),
      };
      break;
    default:
      break;
  }
    return (
        <div className='widget'>
            <div className='left'>
                <span className='title'>{data.icon}</span>
                <span className='counter'>{data.title} </span>
                <span className='link'>{data.link}</span>
            </div>
        </div>
    );
  };
  
  export default Widget;
