import React, { useEffect } from "react";
import { useFormAdmin, Form } from "../../../components/useFormAdmin";
import { Grid, } from '@material-ui/core';
import Controls from "../../../components/controls/Controls";
import * as adminService from "../../../services/adminService";


// const genderItems = [
//     { id: 'action_sur_admins', title: 'Crer et modifier sous-admin' },
//     { id: 'action_sur_associations', title: 'Creer et modifier association' },
//     { id: 'action_sur_membres', title: 'Creer et modifier membre-association' },
// ]

const initialeValues = {
    name: '',
    telephone: '',
    username: '',
    password: '',
    eglise: '',
    statut: '',

}

export default function AdminForm(props) {
    const { addOrEdit, recordForEdit } = props

    const validate = (fieldValues = values) => {
        let temp = { ...errors }
        if ('name' in fieldValues)
            temp.name = fieldValues.name ? "" : "This field is required."
        if ('username' in fieldValues)
            temp.username = fieldValues.username ? "" : "Email is not valid."
        if ('password' in fieldValues)
            temp.password = fieldValues.password ? "" : "This field is required."
        if ('telephone' in fieldValues)
            temp.telephone = fieldValues.telephone.length > 9 ? "" : "Minimum 10 numbers required."
        if ('eglise' in fieldValues)
            temp.eglise = fieldValues.eglise ? "" : "This field is required."
        setErrors({
            ...temp
        })

        if (fieldValues === values)
            return Object.values(temp).every(x => x === "")
    }

    const handleSubmit = e => {
        e.preventDefault()
        if (validate()) {
            addOrEdit(values, resetForm);
        }
    }

    const {
        values,
        setValues,
        errors,
        setErrors,
        handleInputChange,
        resetForm
    } = useFormAdmin(initialeValues, true, validate);

    useEffect(() => {
        if (recordForEdit !== null)
            setValues({
                ...recordForEdit
            })
    }, [recordForEdit])


    return (
        <Form onSubmit={handleSubmit}>
            <Grid container>
                <Grid item xs={6}>
                    <Controls.Input
                        label="Nom & prenom"
                        name="name"
                        value={values.name}
                        onChange={handleInputChange}
                        error={errors.name}
                    />
                    <Controls.Input
                        name="telephone"
                        label="Telephone"
                        value={values.telephone}
                        onChange={handleInputChange}
                        error={errors.telephone}
                    />
                    <Controls.Select
                        label="Statut"
                        name="statut"
                        value={values.statut}
                        onChange={handleInputChange}
                        options={adminService.getStatus()}
                        error={errors.statut}
                    />
                </Grid>
                <Grid item xs={6}>
                    <Controls.Input
                        name="username"
                        label="Nom utilisateur"
                        value={values.username}
                        onChange={handleInputChange}
                        error={errors.username}
                    />
                    <Controls.Input
                        name="password"
                        label="Mot de passe"
                        value={values.password}
                        onChange={handleInputChange}
                        error={errors.password}
                    />
                    <Controls.Input
                        name="eglise"
                        label="Eglise"
                        value={values.eglise}
                        onChange={handleInputChange}
                        error={errors.eglise}
                    />
                    <div>
                        <Controls.Button
                            type="submit"
                            text="VALIDATION" />
                        <Controls.Button
                            text="REDEFINIR"
                            color="default"
                            onClick={resetForm} />
                    </div>
                </Grid>
            </Grid>
        </Form>
    )
}
