import './dashboard.css'
import { AccountBalanceWalletOutlined, KeyboardArrowUp, MonetizationOnOutlined, PersonOutlined, ShoppingCartOutlined } from "@material-ui/icons";
import * as membreService from '../../../services/membreService';
import { useState } from 'react';
import { useEffect } from 'react';

const Widget = ({ type }) => {
const [sumMembre, setSumMembre] = useState([]);
const [currentUser, setCurrentUser] = useState(membreService.getCurrentUser());

const getmembre = () => {
  membreService.getSumMembre()
  .then((res) => {
    setSumMembre(res.data)
  });
}

useEffect(() => {
  getmembre();
}, []);

    let data; 

     //temporary
  const user = sumMembre;
  const diff = 20;
  const empres = 10;
  const reemb = 6;
  const resta = 4;
  switch (type) {
    case "user":
      data = {
        title: "Usuários",
        Money: user,
        link: "Ver todos os usuários",
        icon: (
          <PersonOutlined
            className="icon"
            style={{
              color: "crimson",
              backgroundColor: "rgba(255, 0, 0, 0.2)",
            }}
          />
        ),
      };
      break;
    case "order":
      data = {
        title: "Empréstado",
        Money: empres,
        link: "Todos os empréstimos",
        icon: (
          <ShoppingCartOutlined
            className="icon"
            style={{
              backgroundColor: "rgba(218, 165, 32, 0.2)",
              color: "goldenrod",
            }}
          />
        ),
      };
      break;
    case "earning":
      data = {
        title: "Reembolsado",
        Money: reemb,
        link: "Empréstimos reembolsados",
        icon: (
          <MonetizationOnOutlined
            className="icon"
            style={{ backgroundColor: "rgba(0, 128, 0, 0.2)", color: "green" }}
          />
        ),
      };
      break;
    case "balance":
      data = {
        title: "Não reembolsado",
        Money: resta,
        link: "Empréstimos restantes",
        icon: (
          <AccountBalanceWalletOutlined
            className="icon"
            style={{
              backgroundColor: "rgba(128, 0, 128, 0.2)",
              color: "purple",
            }}
          />
        ),
      };
      break;
    default:
      break;
  }
    return (
        <div className='widget'>
            <div className='left'>
                <span className='title'>{data.title}</span>
                <span className='counter'>{data.Money} </span>
                <span className='link'>{data.link}</span>
            </div>
            <div className='right'>
                <div className='percentage positive'>
                    <KeyboardArrowUp />
                    {diff} %</div>
                    {data.icon}
            </div>
        </div>
    );
  };
  
  export default Widget;
