import React, {useEffect, useState } from 'react'
import { Paper, makeStyles, TableBody, TableRow, TableCell, Toolbar, InputAdornment } from '@material-ui/core';
import * as membreService from "../../../services/membreService";
import Controls from "../../../components/controls/Controls";
import { Search } from "@material-ui/icons";
import AddIcon from '@material-ui/icons/Add';
import Popup from "../../../components/Popup";
import EditOutlinedIcon from '@material-ui/icons/EditOutlined';
import MembreForm from './MembreForm';
import useTableMembre from '../../../components/useTableMembre';
import SideMenu from '../../../components/SideMenu';
import Header from '../../../components/Header';

const useStyles = makeStyles(theme => ({
    pageContent: {
        margin: theme.spacing(1),
        padding: theme.spacing(1)
    },
    searchInput: {
        width: '60%'
    },
    newButton: {
        position: 'absolute',
        right: '10px'
    }
}))


const headCells = [
    { id: 'name', label: 'Sobrenome' },
    { id: 'telephone', label: 'Telefone' },
    { id: 'profession', label: 'Ocupação' },
    { id: 'statut', label: 'Status' },
    { id: 'actions', label: 'Estoque', disableSorting: true }
]

export default function Membres() {

    const classes = useStyles();
    const [recordForEdit, setRecordForEdit] = useState(null)
    const [records, setRecords] = useState([]);
    const [currentUser, setCurrentUser] = useState(adminService.getCurrentUser());

    const getmembre = () => {
        (currentUser ? (currentUser.roles.toString() === "ROLE_MODERATOR") ? 
        (membreService.getMembre(currentUser.matricule)) : (membreService.getAllMembre()) : (membreService.getAllMembre()))
        .then((res) => {
            setRecords(res.data)
        });
    }
    
    useEffect(() => {
        getmembre();
    }, []);

    const [filterFn, setFilterFn] = useState({ fn: records => { return records; } })
    const [openPopup, setOpenPopup] = useState(false)
    // const [notify, setNotify] = useState({ isOpen: false, message: '', type: '' })
    // const [confirmDialog, setConfirmDialog] = useState({ isOpen: false, title: '', subTitle: '' })

    const {
        TblContainer,
        TblHead,
        TblPagination,
        recordsAfterPagingAndSorting
    } = useTableMembre(records, headCells, filterFn);

    const handleSearch = e => {
        let target = e.target;
        setFilterFn({
            fn: records => {
                if (target.value === "")
                    return records;
                else
                    return records.filter(x => x.name.toLowerCase().includes(target.value))
            }
        })
    }

    const addOrEdit = (membre, resetForm) => {
        {(membre.id) ? 
            membreService.updateMembre(membre.id, membre) :

            console.log(membre)
            console.log(membre.id)
        }
        if(!membre.id) {
            membreService.insertMembre(membre);
            console.log(membre)
            console.log(membre.id)
        }
        // setNotify({
        //     isOpen: true,
        //     message: 'Submit Successfully',
        //     type: 'success'
        // })
        resetForm()
        setRecordForEdit(null)
        setOpenPopup(false)
        getmembre()
    }

    const openInPopup = membre => {
        setRecordForEdit(membre)
        setOpenPopup(true)
    }

    return (
        <>
            <Header />
            <Paper className={classes.pageContent}>

                <Toolbar>
                    <Controls.Input
                        label="Pesquisar Membro"
                        className={classes.searchInput}
                        InputProps={{
                            startAdornment: (<InputAdornment position="start">
                                <Search />
                            </InputAdornment>)
                        }}
                        onChange={handleSearch}
                    />
                    <Controls.Button
                        text="Adicionar"
                        variant="outlined"
                        startIcon={<AddIcon />}
                        className={classes.newButton}
                        onClick={() => { setOpenPopup(true); setRecordForEdit(null); }}
                    />
                </Toolbar>
                <TblContainer>
                    <TblHead />
                    <TableBody>
                        {
                            recordsAfterPagingAndSorting().map(membre =>
                                (<TableRow key={membre.id}>
                                    <TableCell>{membre.name}</TableCell>
                                    <TableCell>{membre.telephone}</TableCell>
                                    <TableCell>{membre.profession}</TableCell>
                                    <TableCell>{membre.statut}</TableCell>
                                    <TableCell>
                                        <Controls.ActionButton
                                            color="primary"
                                            onClick={() => { openInPopup(membre) }}>
                                            <EditOutlinedIcon fontSize="small" />
                                        </Controls.ActionButton>
                                    </TableCell>
                                </TableRow>)
                            )
                        }
                    </TableBody>
                </TblContainer>
                <TblPagination />
            </Paper>
            <Popup
                title="Membro Formato"
                openPopup={openPopup}
                setOpenPopup={setOpenPopup}
            >
                <MembreForm
                    recordForEdit={recordForEdit}
                    addOrEdit={addOrEdit} />
            </Popup>
            {/* <Notification
                notify={notify}
                setNotify={setNotify}
            /> */}
        </>
    )
}
