import React, { useState } from 'react'
import { withStyles } from "@material-ui/core";
import "../App/App.css"
import { SideMenuData, SideMenuDataUser } from './SideMenuData';
import * as authService from '../services/authService'

// withStyles & makeStyles

const style = {
    sideMenu: {
        display: 'flex',
        flexDirection: 'column',
        position: 'absolute',
        left: '0px',
        width: '250px',
        height: '100%',
        backgroundColor: '#f4661b',
        color: "white",

    },
    img: {
        position: 'center',
        padding: '0px',
        width: '150px',
        margin: '2rem'

    }
}

const SideMenu = (props) => {
    const { classes } = props;
    const {role1 } = "ROLE_ADMIN";
    const {role2 } = "ROLE_USER";
    const {role3 } = "ROLE_MODERATOR";
    const [currentUser, setCurrentUser] = useState(authService.getCurrentUser());
    console.log(currentUser)

    return (
        <div className={classes.sideMenu}>
            <img className={classes.img} src='logo.png'/>
            {(currentUser) ? null : null}
            {(currentUser.roles === role2) ? 
            <ul className='SibeMenuList'>
                {SideMenuData.map((val, key) => {
                    return (
                        <li 
                            key={key} 
                            className='row'
                            id={window.location.pathname === val.link ? "active" : ""}
                            onClick={() => {
                                window.location.pathname = val.link
                            }}>
                            <div id='icon'>{val.icon}</div>
                            <div id='title'>
                                {val.title}
                            </div>
                        </li>
                    )
                })}
            </ul>: 
            null}
            {(currentUser.roles === role2) ? 
            <ul className='SibeMenuList'>
                {SideMenuDataUser.map((val, key) => {
                    return (
                        <li 
                            key={key} 
                            className='row'
                            id={window.location.pathname === val.link ? "active" : ""}
                            onClick={() => {
                                window.location.pathname = val.link
                            }}>
                            <div id='icon'>{val.icon}</div>
                            <div id='title'>
                                {val.title}
                            </div>
                        </li>
                    )
                })}
            </ul>: 
            null}
        </div>
    )
}

export default withStyles(style)(SideMenu);
