import React, { useEffect } from 'react'
import { Grid, } from '@material-ui/core';
import Controls from "../../components/controls/Controls";
import { useForm, Form } from '../../components/useForm';
import * as associationService from "../../services/associationService";


// const genderItems = [
//     { id: 'male', title: 'Male' },
//     { id: 'female', title: 'Female' },
//     { id: 'other', title: 'Other' },
// ]

const initialFValues = {
    association_name: '',
    description: '',
    name: '',
    email: '',
    password: '',
    telephone: '',
    statut: '',
    disable: true,
    creationdatetime: new Date(),
}

export default function AssociationForm(props) {
    const { addOrEdit, recordForEdit } = props

    const validate = (fieldValues = values) => {
        let temp = { ...errors }
        if ('association_name' in fieldValues)
            temp.association_name = fieldValues.association_name ? "" : "This field is required."
        if ('description' in fieldValues)
            temp.description = fieldValues.description ? "" : "This field is required."
        if ('name' in fieldValues)
            temp.name = fieldValues.name ? "" : "This field is required."
        if ('email' in fieldValues)
            temp.email = (/$^|.+@.+..+/).test(fieldValues.email) ? "" : "Email is not valid."
        if ('password' in fieldValues)
            temp.password = fieldValues.password.length >= 7 ? "" : "Minimum 8 characters required."
        if ('telephone' in fieldValues)
            temp.telephone = fieldValues.telephone.length > 9 ? "" : "Minimum 10 numbers required."
        if ('statut' in fieldValues)
            temp.statut = fieldValues.statut.length !== 0 ? "" : "This field is required."
        setErrors({
            ...temp
        })

        if (fieldValues === values)
            return Object.values(temp).every(x => x === "")
    }

    const {
        values,
        setValues,
        errors,
        setErrors,
        handleInputChange,
        resetForm
    } = useForm(initialFValues, true, validate);

    const handleSubmit = e => {
        e.preventDefault()
        if (validate()) {
            addOrEdit(values, resetForm);
        }
    }

    useEffect(() => {
        if (recordForEdit !== null)
            setValues({
                ...recordForEdit
            })
    }, [recordForEdit])

    return (
        <Form onSubmit={handleSubmit}>
            <Grid container>
                <Grid item xs={6}>
                    <Controls.Input
                        name="association_name"
                        label="Associação"
                        value={values.association_name}
                        onChange={handleInputChange}
                        error={errors.association_name}
                    />
                    <Controls.Input
                        label="Descrição"
                        name="description"
                        value={values.description}
                        onChange={handleInputChange}
                        error={errors.description}
                    />
                    <Controls.Select
                        label="Status"
                        name="statut"
                        value={values.statut}
                        onChange={handleInputChange}
                        options={associationService.getDepartmentCollection()}
                        error={errors.statut}
                    />
                    <Controls.Input
                        name="telephone"
                        label="Telefone Administrador"
                        value={values.telephone}
                        onChange={handleInputChange}
                        error={errors.telephone}
                        disabled = {values.disable}
                    />
                </Grid>
                <Grid item xs={6}>
                    <Controls.Input
                        name="name"
                        label="Sobrenome Administrador"
                        value={values.name}
                        onChange={handleInputChange}
                        error={errors.name}
                    />
                    <Controls.Input
                        name="email"
                        label="E-mail Administrador"
                        value={values.email}
                        onChange={handleInputChange}
                        error={errors.email}
                    />
                    <Controls.Input
                        name="password"
                        label="Senha"
                        type="password"
                        value={values.password}
                        onChange={handleInputChange}
                        error={errors.password}
                    />
                    <br/><br/>
                    <div>
                        <Controls.Button
                            type="submit"
                            text="CRIO" />
                        <Controls.Button
                            text="REDEFINIR"
                            color="default"
                            onClick={resetForm} />
                    </div>
                </Grid>
            </Grid>
        </Form>
    )
}
