import React, {useEffect, useState } from 'react'
import AssociationForm from "./AssociationForm";
import { Paper, makeStyles, TableBody, TableRow, TableCell, Toolbar, InputAdornment } from '@material-ui/core';
import useTable from "../../components/useTable";
import * as associationService from "../../services/associationService";
import Controls from "../../components/controls/Controls";
import { Search } from "@material-ui/icons";
import AddIcon from '@material-ui/icons/Add';
import Popup from "../../components/Popup";
import EditOutlinedIcon from '@material-ui/icons/EditOutlined';
import SideMenu from '../../components/SideMenu';
import Header from '../../components/Header';
import EventBus from '../../common/EventBus';
import * as authService from '../../services/authService'
import { Redirect } from 'react-router-dom';

const useStyles = makeStyles(theme => ({
    pageContent: {
        margin: theme.spacing(1),
        padding: theme.spacing(1)
    },
    searchInput: {
        width: '60%'
    },
    newButton: {
        position: 'absolute',
        right: '10px'
    }
}))


const headCells = [
    { id: 'matricule', label: 'Numero registro' },
    { id: 'association_name', label: 'Associação' },
    { id: 'name', label: 'Sobrenome Administrador' },
    { id: 'statut', label: 'Status' },
    { id: 'actions', label: 'Estoque', disableSorting: true }
]

export default function Associations(props) {
    const [currentUser, setCurrentUser] = useState(associationService.getCurrentUser());
    const [redirect, setRedirect] = useState(null);
    const [userReady, setUserReady] = useState(true);

    const classes = useStyles();
    const [recordForEdit, setRecordForEdit] = useState(null)
    const [records, setRecords] = useState([]);

    const getAllassociation = () => {
        associationService.getAllAssociation()
        .then((res) => {
            setRecords(res.data) 
        }
        );
    }
    console.log(currentUser)
    useEffect(() => {
        getAllassociation();
    }, []);

    const [filterFn, setFilterFn] = useState({ fn: records => { return records; } })
    const [openPopup, setOpenPopup] = useState(false)
    // const [notify, setNotify] = useState({ isOpen: false, message: '', type: '' })
    // const [confirmDialog, setConfirmDialog] = useState({ isOpen: false, title: '', subTitle: '' })

    const {
        TblContainer,
        TblHead,
        TblPagination,
        recordsAfterPagingAndSorting
    } = useTable(records, headCells, filterFn);

    const handleSearch = e => {
        let target = e.target;
        setFilterFn({
            fn: records => {
                if (target.value === "")
                    return records;
                else
                    return records.filter(x => x.association_name.toLowerCase().includes(target.value))
            }
        })
    }

    const addOrEdit = (association, resetForm) => {
        {(association.id) ?
            associationService.updateAssociation(association.id, association) :

            console.log(association)
            console.log(association.id)
        }
        if(!association.id) {
            associationService.insertAssociation(association);
        console.log(association)
        console.log(association.id)
        }
        // setNotify({
        //     isOpen: true,
        //     message: 'Submit Successfully',
        //     type: 'success'
        // })
        resetForm()
        setRecordForEdit(null)
        setOpenPopup(false)
        getAllassociation()
    }

    const openInPopup = association => {
        setRecordForEdit(association)
        setOpen
        Popup(true)
    }


    const red = "/";
    if(currentUser) { 

       redirect = 1;
    }

    return (
        <>

            <Header />
            <Paper className={classes.pageContent}>
                <Toolbar>
                    <Controls.Input
                        label="Pesquisar Associação"
                        className={classes.searchInput}
                        InputProps={{
                            startAdornment: (<InputAdornment position="start">
                                <Search />
                            </InputAdornment>)
                        }}
                        onChange={handleSearch}
                    />
                    <Controls.Button
                        text="Adicionar"
                        variant="outlined"
                        startIcon={<AddIcon />}
                        className={classes.newButton}
                        onClick={() => { setOpenPopup(true); setRecordForEdit(null); }}
                    />
                </Toolbar>
                <TblContainer>
                    <TblHead />
                    <TableBody>
                        {
                            recordsAfterPagingAndSorting().map(association =>
                                (<TableRow key={association.id}>
                                    <TableCell>{association.matricule}</TableCell>
                                    <TableCell>{association.association_name}</TableCell>
                                    <TableCell>{association.name}</TableCell>
                                    <TableCell>{association.statut}</TableCell>
                                    <TableCell>
                                        <Controls.ActionButton
                                            color="primary"
                                            onClick={() => { openInPopup(association) }}>
                                            <EditOutlinedIcon fontSize="small" />
                                        </Controls.ActionButton>
                                    </TableCell>
                                </TableRow>)
                            )
                        }
                    </TableBody>
                </TblContainer>
                <TblPagination />
            </Paper>
            <Popup
                title="Associação Formato"
                openPopup={openPopup}
                setOpenPopup={setOpenPopup}
            >
                <AssociationForm
                    recordForEdit={recordForEdit}
                    addOrEdit={addOrEdit} />
            </Popup>
            {/* <Notification
                notify={notify}
                setNotify={setNotify}
            /> */}
        </>
    )
}
