import React, {useEffect, useState } from 'react'
import { Paper, makeStyles, TableBody, TableRow, TableCell, Toolbar, InputAdornment } from '@material-ui/core';
import * as adminService from "../../../services/adminService";
import Controls from "../../../components/controls/Controls";
import { Search } from "@material-ui/icons";
import AddIcon from '@material-ui/icons/Add';
import Popup from "../../../components/Popup";
import EditOutlinedIcon from '@material-ui/icons/EditOutlined';
import AdminForm from './EgliseForm';
import useTableAdmin from '../../../components/useTableAdmin';
import Header from '../../../components/Header';

const useStyles = makeStyles(theme => ({
    pageContent: {
        margin: theme.spacing(1),
        padding: theme.spacing(1)
    },
    searchInput: {
        width: '50%',
        right: '1rem'
    },
    newButton: {
        position: 'absolute',
        right: '10px'
    },
    newButton1: {
        position: 'absolute',
        right: '9rem'
    }
}))


const headCells = [
    { id: 'eglise', label: 'Eglise Local' },
    { id: 'cota', label: 'Montant Cotisation' },
    { id: 'versement', label: 'Versement' },
    { id: 'dateVersement', label: 'Date' },
    { id: 'actions', label: 'Action', disableSorting: true }
]

export default function Eglise() {

    const classes = useStyles();
    const [recordForEdit, setRecordForEdit] = useState(null)
    const [records, setRecords] = useState([]);
    const [currentUser, setCurrentUser] = useState(adminService.getCurrentUser());

    const getAlladmin = () => {
        (currentUser ? (currentUser.roles.toString() === "ROLE_ZONE") ? 
        (adminService.getAllLocal(currentUser.eglise)) : (adminService.getAllZone(currentUser.eglise)) : (adminService.getAllRegion(currentUser.eglise)))
        
        .then((res) => {
            setRecords(res.data)
        });
    }
    
    useEffect(() => {
        getAlladmin();
    }, []);

    const [filterFn, setFilterFn] = useState({ fn: records => { return records; } })
    const [openPopup, setOpenPopup] = useState(false)
    // const [notify, setNotify] = useState({ isOpen: false, message: '', type: '' })
    // const [confirmDialog, setConfirmDialog] = useState({ isOpen: false, title: '', subTitle: '' })

    const {
        TblContainer,
        TblHead,
        TblPagination,
        recordsAfterPagingAndSorting
    } = useTableAdmin(records, headCells, filterFn);

    const handleSearch = e => {
        let target = e.target;
        setFilterFn({
            fn: records => {
                if (target.value === "")
                    return records;
                else
                    return records.filter(x => x.eglise.toLowerCase().includes(target.value))
            }
        })
    }

    const addOrEdit = (user, resetForm) => {
        // eslint-disable-next-line no-lone-blocks
        {(user.id) ?
            adminService.updateAdmin(user.id, user) :

            console.log(user)
            console.log(user.id)
        }
        if(!user.id) {
            adminService.insertAdmin(user);
            console.log(user)
            console.log(user.id)
        }
        // setNotify({
        //     isOpen: true,
        //     message: 'Submit Successfully',
        //     type: 'success'
        // })
        resetForm()
        setRecordForEdit(null)
        setOpenPopup(false)
        getAlladmin()
    }

    const openInPopup = user => {
        setRecordForEdit(user)
        setOpenPopup(true)
    }

    return (
        <>
            <Header />
            <Paper className={classes.pageContent}>

                <Toolbar>
                    <Controls.Input
                        label="Recherche"
                        className={classes.searchInput}
                        InputProps={{
                            startAdornment: (<InputAdornment position="start">
                                <Search />
                            </InputAdornment>)
                        }}
                        onChange={handleSearch}
                    />
                    <Controls.Button
                        text="Ajouter"
                        variant="outlined"
                        startIcon={<AddIcon />}
                        className={classes.newButton}
                        onClick={() => { setOpenPopup(true); setRecordForEdit(null); }}
                    />
                </Toolbar>
                <TblContainer>
                    <TblHead />
                    <TableBody>
                        {
                            recordsAfterPagingAndSorting().map(user =>
                                (<TableRow key={user.id}>
                                    <TableCell>{user.eglise}</TableCell>
                                    <TableCell>{user.cota}</TableCell>
                                    <TableCell>{user.versement}</TableCell>
                                    <TableCell>{user.creationdate}</TableCell>
                                    <TableCell>
                                        <Controls.ActionButton
                                            color="primary"
                                            onClick={() => { openInPopup(user) }}>
                                            <EditOutlinedIcon fontSize="small" />
                                        </Controls.ActionButton>
                                    </TableCell>
                                </TableRow>)
                            )
                        }
                    </TableBody>
                </TblContainer>
                <TblPagination />
            </Paper>
            <Popup
                title="Formulaire Eglise"
                openPopup={openPopup}
                setOpenPopup={setOpenPopup}
            >
                <AdminForm
                    recordForEdit={recordForEdit}
                    addOrEdit={addOrEdit} />
            </Popup>
            {/* <Notification
                notify={notify}
                setNotify={setNotify}
            /> */}
        </>
    )
}
