import React, { useState, useEffect } from 'react'
import { Grid, } from '@material-ui/core';
import Controls from "../../components/controls/Controls";
import { useForm, Form } from '../../components/useForm';
import * as employeeService from "../../services/associationService";


// const genderItems = [
//     { id: 'male', title: 'Male' },
//     { id: 'female', title: 'Female' },
//     { id: 'other', title: 'Other' },
// ]

const initialFValues = {
    //id: 0,
    association_name: '',
    description: '',
    nameAdmin: '',
    emailAdmin: '',
    motdepasse: '',
    telephoneAdmin: '',
    statut: '',
    creationdatetime: new Date(),
}

export default function AssociationForm(props) {
    const { addOrEdit, recordForEdit } = props

    const validate = (fieldValues = values) => {
        let temp = { ...errors }
        if ('association_name' in fieldValues)
            temp.association_name = fieldValues.association_name ? "" : "This field is required."
        if ('description' in fieldValues)
            temp.description = fieldValues.description ? "" : "This field is required."
        if ('nameAdmin' in fieldValues)
            temp.nameAdmin = fieldValues.nameAdmin ? "" : "This field is required."
        if ('emailAdmin' in fieldValues)
            temp.emailAdmin = (/$^|.+@.+..+/).test(fieldValues.emailAdmin) ? "" : "Email is not valid."
        if ('motdepasse' in fieldValues)
            temp.motdepasse = fieldValues.motdepasse ? "" : "This field is required."
        if ('telephoneAdmin' in fieldValues)
            temp.telephoneAdmin = fieldValues.telephoneAdmin.length > 9 ? "" : "Minimum 10 numbers required."
        if ('statut' in fieldValues)
            temp.statut = fieldValues.statut.length !== 0 ? "" : "This field is required."
        setErrors({
            ...temp
        })

        if (fieldValues === values)
            return Object.values(temp).every(x => x === "")
    }

    const {
        values,
        setValues,
        errors,
        setErrors,
        handleInputChange,
        resetForm
    } = useForm(initialFValues, true, validate);

    const handleSubmit = e => {
        e.preventDefault()
        if (validate()) {
            addOrEdit(values, resetForm);
        }
    }

    useEffect(() => {
        if (recordForEdit !== null)
            setValues({
                ...recordForEdit
            })
    }, [recordForEdit])

    return (
        <Form onSubmit={handleSubmit}>
            <Grid container>
                <Grid item xs={6}>
                    <Controls.Input
                        name="association_name"
                        label="Association Name"
                        value={values.association_name}
                        onChange={handleInputChange}
                        error={errors.association_name}
                    />
                    <Controls.Input
                        label="Description"
                        name="description"
                        value={values.description}
                        onChange={handleInputChange}
                        error={errors.description}
                    />
                    <Controls.Select
                        label="Status"
                        name="statut"
                        value={values.statut}
                        onChange={handleInputChange}
                        options={employeeService.getDepartmentCollection()}
                        error={errors.statut}
                    />
                    <Controls.DatePicker
                        label="Creation Date"
                        name="creationdatetime"
                        value={values.creationdatetime}
                        onChange={handleInputChange}
                    />
                </Grid>
                <Grid item xs={6}>
                    <Controls.Input
                        name="nameAdmin"
                        label="Name Admin"
                        value={values.nameAdmin}
                        onChange={handleInputChange}
                        error={errors.nameAdmin}
                    />
                    <Controls.Input
                        name="emailAdmin"
                        label="Email Admin"
                        value={values.emailAdmin}
                        onChange={handleInputChange}
                        error={errors.emailAdmin}
                    />
                    <Controls.Input
                        name="motdepasse"
                        label="Password"
                        type="password"
                        value={values.motdepasse}
                        onChange={handleInputChange}
                        error={errors.motdepasse}
                    />
                    <Controls.Input
                        name="telephoneAdmin"
                        label="Telephone Admin"
                        value={values.telephoneAdmin}
                        onChange={handleInputChange}
                        error={errors.telephoneAdmin}
                    />

                    <div>
                        <Controls.Button
                            type="submit"
                            text="CREATE" />
                        <Controls.Button
                            text="Reset"
                            color="default"
                            onClick={resetForm} />
                    </div>
                </Grid>
            </Grid>
        </Form>
    )
}
