import React, { useState, useEffect } from 'react'
import { Grid, } from '@material-ui/core';
import Controls from "../../components/controls/Controls";
import { useForm, Form } from '../../components/useForm';
import * as employeeService from "../../services/employeeService";


// const genderItems = [
//     { id: 'male', title: 'Male' },
//     { id: 'female', title: 'Female' },
//     { id: 'other', title: 'Other' },
// ]

const initialFValues = {
    //id: 0,
    association_name: '',
    descripstion: '',
    nameAdmin: '',
    emailAdmin: '',
    motdepasse: '',
    telephoneAdmin: '',
    statut: '',
    creaiondatetime: new Date(),
}

export default function EmployeeForm(props) {
    const { addOrEdit, recordForEdit } = props

    const validate = (fieldValues = values) => {
        let temp = { ...errors }
        if ('association_name' in fieldValues)
            temp.fullName = fieldValues.fullName ? "" : "This field is required."
        if ('description' in fieldValues)
            temp.fullName = fieldValues.fullName ? "" : "This field is required."
        if ('nameAdmin' in fieldValues)
            temp.fullName = fieldValues.fullName ? "" : "This field is required."
        if ('email' in fieldValues)
            temp.email = (/$^|.+@.+..+/).test(fieldValues.email) ? "" : "Email is not valid."
        if ('motdepasse' in fieldValues)
            temp.fullName = fieldValues.fullName ? "" : "This field is required."
        if ('telephoneAdmin' in fieldValues)
            temp.mobile = fieldValues.mobile.length > 9 ? "" : "Minimum 10 numbers required."
        if ('statut' in fieldValues)
            temp.departmentId = fieldValues.departmentId.length !== 0 ? "" : "This field is required."
        setErrors({
            ...temp
        })

        if (fieldValues === values)
            return Object.values(temp).every(x => x === "")
    }

    const {
        values,
        setValues,
        errors,
        setErrors,
        handleInputChange,
        resetForm
    } = useForm(initialFValues, true, validate);

    const handleSubmit = e => {
        e.preventDefault()
        if (validate()) {
            addOrEdit(values, resetForm);
        }
    }

    useEffect(() => {
        if (recordForEdit !== null)
            setValues({
                ...recordForEdit
            })
    }, [recordForEdit])

    return (
        <Form onSubmit={handleSubmit}>
            <Grid container>
                <Grid item xs={6}>
                    <Controls.Input
                        name="association_name"
                        label="Association Name"
                        value={values.association_name}
                        onChange={handleInputChange}
                        error={errors.association_name}
                    />
                    <Controls.Input
                        label="Description"
                        name="description"
                        value={values.descripstion}
                        onChange={handleInputChange}
                        error={errors.descripstion}
                    />
                    <Controls.Select
                        label="Status"
                        name="statut"
                        value={values.statut}
                        onChange={handleInputChange}
                        error={errors.statut}
                    />
                    <Controls.DatePicker
                        label="Creation Date"
                        name="creationdatetime"
                        value={values.creaiondatetime}
                        onChange={handleInputChange}
                    />
                </Grid>
                <Grid item xs={6}>
                    <Controls.Input
                        name="nameAdmin"
                        label="Name Admin"
                        value={values.nameAdmin}
                        onChange={handleInputChange}
                        error={errors.nameAdmin}
                    />
                    <Controls.Input
                        name="emailAdmin"
                        label="Email Admin"
                        value={values.emailAdmin}
                        onChange={handleInputChange}
                        error={errors.emailAdmin}
                    />
                    <Controls.Input
                        name="motdepasse"
                        label="Password"
                        value={values.motdepasse}
                        onChange={handleInputChange}
                        error={values.motdepasse}
                    />
                    <Controls.Input
                        name="telphoneAdmin"
                        label="Telephone Admin"
                        value={values.telephoneAdmin}
                        onChange={handleInputChange}
                        error={values.telephoneAdmin}
                    />

                    <div>
                        <Controls.Button
                            type="submit"
                            text="CREATE" />
                        <Controls.Button
                            text="Reset"
                            color="default"
                            onClick={resetForm} />
                    </div>
                </Grid>
            </Grid>
        </Form>
    )
}
