import React from "react";
import { useFormAdmin } from "../../components/useFormAdmin";
import { Grid, } from '@material-ui/core';
import Controls from "../../components/controls/Controls";
import { useForm, Form } from '../../components/useForm';
import * as associationService from "../../services/associationService";


// const genderItems = [
//     { id: 'action_sur_admins', title: 'Crer et modifier sous-admin' },
//     { id: 'action_sur_associations', title: 'Creer et modifier association' },
//     { id: 'action_sur_membres', title: 'Creer et modifier membre-association' },
// ]

const initialeValues = {
    matricule: '',
    name: '',
    telephone: '',
    email: '',
    motdepasse: '',
    role: '',
    autorisations: '',
    statut: '',
    creationdatetime: new Date()
}

export default function AdminForm(props) {
    const { addOrEdit, recordForEdit } = props

    const validate = (fieldValues = values) => {
        let temp = { ...errors }
        if ('name' in fieldValues)
            temp.name = fieldValues.name ? "" : "This field is required."
        if ('email' in fieldValues)
            temp.email = (/$^|.+@.+..+/).test(fieldValues.email) ? "" : "Email is not valid."
        if ('motdepasse' in fieldValues)
            temp.motdepasse = fieldValues.motdepasse ? "" : "This field is required."
        if ('telephone' in fieldValues)
            temp.telephone = fieldValues.telephone.length > 9 ? "" : "Minimum 10 numbers required."
        if ('role' in fieldValues)
            temp.role = fieldValues.role ? "" : "This field is required."
        if ('nameAdmin' in fieldValues)
            temp.nameAdmin = fieldValues.nameAdmin ? "" : "This field is required."
        if ('statut' in fieldValues)
            temp.statut = fieldValues.statut.length !== 0 ? "" : "This field is required."
        if ('autorisations' in fieldValues)
            temp.autorisations = fieldValues.autorisations ? "" : "This field is required."
        setErrors({
            ...temp
        })

        if (fieldValues === values)
            return Object.values(temp).every(x => x === "")
    }

    const handleSubmit = e => {
        e.preventDefault()
        if (validate()) {
            addOrEdit(values, resetForm);
        }
    }

    const {
        values,
        setValues,
        errors,
        setErrors,
        handleInputChange,
        resetForm
    } = useFormAdmin(initialeValues, true, validate);


    return (
        <Form onSubmit={handleSubmit}>
            <Grid container>
                <Grid item xs={6}>
                    <Controls.Input
                        name="matricule"
                        label="Numero registro"
                        value={values.matricule}
                        //onChange={handleInputChange}
                    />
                    <Controls.Input
                        label="Sobrenome"
                        name="name"
                        value={values.name}
                        onChange={handleInputChange}
                        error={errors.name}
                    />
                    <Controls.Select
                        label="Status"
                        name="statut"
                        value={values.statut}
                        onChange={handleInputChange}
                        options={associationService.getDepartmentCollection()}
                        error={errors.statut}
                    />
                    <Controls.DatePicker
                        label="Data Criaçao"
                        name="creationdatetime"
                        value={values.creationdatetime}
                        onChange={handleInputChange}
                    />
                </Grid>
                <Grid item xs={6}>
                    <Controls.Input
                        name="telephone"
                        label="Telefone"
                        value={values.telephone}
                        onChange={handleInputChange}
                        error={errors.telephone}
                    />
                    <Controls.Input
                        name="email"
                        label="E-mail"
                        value={values.email}
                        onChange={handleInputChange}
                        error={errors.email}
                    />
                    <Controls.Input
                        name="motdepasse"
                        label="Senha"
                        type="password"
                        value={values.motdepasse}
                        onChange={handleInputChange}
                        error={errors.motdepasse}
                    />
                    <Controls.Select
                        name="role"
                        label="Funçao"
                        value={values.role}
                        onChange={handleInputChange}
                        options={associationService.getDepartmentCollection()}
                        error={errors.role}
                    />
                    <Controls.Checkbox />
                    <div>
                        <Controls.Button
                            type="submit"
                            text="CRIO" />
                        <Controls.Button
                            text="REDEFINIR"
                            color="default"
                            onClick={resetForm} />
                    </div>
                </Grid>
            </Grid>
        </Form>
    )
}
